Property.Value:

BEGIN  

	DECLARE @orderDetailIdentifier INT

	INSERT INTO OrderDetail 
	(	
		 OrderIdentifier
		,ExternalIdentifier
		,[Description]
		,KeyWords
		,Title
		,Author
		,Publisher
		,OrderStatusId
		,CreatedDate
	)
	VALUES
	(
		@pi_OrderIdentifier,
		@pc_ExternalIdentifier,
		@pc_Description,
		@pc_KeyWords,
		@pc_Title,
		@pc_Author,
		@pc_Publisher,
		@pi_OrderStatusId,
		GETDATE()
	)

	SET @orderDetailIdentifier = SCOPE_IDENTITY()
	SELECT @orderDetailIdentifier
END
